INSERT INTO `toon_ez_categories` VALUES(1, 3, 'Mugs & Water Bottles', '1_11_oz_White_Mug_(10102T).jpg', '<p>Our photo mugs make great gifts for parents, grandparents, friends and relatives.</p>\r\n<ul>\r\n<li><span>All mugs are microwave and dishwasher safe.</span></li>\r\n<li><span>11oz available in Black or white, 15oz available in white.</span></li>\r\n</ul>\r\n<p>Personalized water bottle, with your photo.</p>\r\n<ul>\r\n<li><span>20 oz. White Stainless steel</span></li>\r\n<li><span>Includes screw on stopper top and carabiner.</span></li>\r\n<li><span>Hand wash only.</span></li>\r\n</ul>', 1, 1);
INSERT INTO `toon_ez_categories` VALUES(2, 2, 'T-Shirts', '2_tshirt_(51306).jpg', '<p>Personalize your t-shirt with your own image or use one our designs!</p>\r\n<ul>\r\n<li><span>100% Fine Jersey cotton construction </span></li>\r\n<li><span>Durable rib neckband </span></li>\r\n<li><span>Image won&rsquo;t fade or crack when washed according to instructions.</span></li>\r\n<li><span>Women&rsquo;s shirts available in small to xlarge</span></li>\r\n<li><span>Men&rsquo;s shirts available in small to xxlarge</span></li>\r\n<li><span>Kids shirts available in toddler 2-6 and youth 8-12</span></li>\r\n</ul>', 1, 1);
INSERT INTO `toon_ez_categories` VALUES(5, 1, 'Ceramic Tiles', '5_4x4_Ceramic_Tiles_(10120).png', '<p>A unique way to display your photo memories.</p>\r\n<ul>\r\n<li><span>The 6"x6" ceramic tile is printed with your unique photo.</span></li>\r\n<li><span>Or, a set of six 4.25"x4.25" tiles is printed with your photo, to achieve a mural effect.</span></li>\r\n</ul>', 1, 5);
INSERT INTO `toon_ez_categories` VALUES(6, 1, 'Rolled Posters', '6_rolled_poster_(90400).jpg', '<p>Rolled posters are printed on a inkjet enhanced matte paper and 														laminated. The enhanced matte is a heavyweight stock that 														delivers true photographic feel and has a high color gamut that 														yields remarkable color reproduction.</p>', 1, 4);
INSERT INTO `toon_ez_categories` VALUES(10, 1, 'Framed Posters', '10_24x36_FP_brown_(82226).png', '<p>A lightweight alternative to canvas, ready to hang framed poster features your image on high-quality matte poster stock.</p>\r\n<ul>\r\n<li> <span>Printed image is mounted onto a masonite board approximately 3/8" thick and set into a sleek wooden box frame.</span> </li>\r\n<li> <span>Frame colors are black or chocolate. </span> </li>\r\n<li> <span>Pefect for office, dorm room, classroom, home office, childrens'' rooms.</span> </li>\r\n</ul>', 1, 2);
INSERT INTO `toon_ez_categories` VALUES(15, 4, 'Cards ', '15_Folded_Cards_(70270).png', '<p>Classic folded cards with your image on front.</p>\r\n<ul>\r\n<li><span>Heavy 110# paper stock ensures excellent image reproduction.</span></li>\r\n<li><span>These 5&rdquo;x7&rdquo; or 3.5&rdquo;x5&rdquo; cards are missives you will be proud to send to friends and family. </span></li>\r\n</ul>', 1, 2);
INSERT INTO `toon_ez_categories` VALUES(16, 4, 'Stickers', '16_Sticker_(73005).jpg', '<p>Your chosen image prints with excellent color reproduction pre-cut on one sheet.</p>\r\n<ul>\r\n<li> <span>These stickers are a great way to add a personalized touch to your greetings or to label your children''s belongings.</span> </li>\r\n</ul>', 1, 4);
INSERT INTO `toon_ez_categories` VALUES(17, 1, 'Framed Canvas', '17_30x40_FCb_black_(82111).png', '<p>Luxurious and personal &ndash; this fine archival quality canvas is printed and set into a sleek wooden box frame.</p>\r\n<ul>\r\n<li><span>Frame colors are black or chocolate.</span></li>\r\n<li><span>This beautiful product arrives ready  to hang on your home or office wall &ndash; a great gift idea and perfect for  busy parents, professionals and students. </span></li>\r\n</ul>', 1, 1);
INSERT INTO `toon_ez_categories` VALUES(18, 1, 'Gallery Wrapped Canvas', '18_main_image.jpg', '<p>Simple and elegant &ndash; this is a fine archival quality canvas.</p>\r\n<ul>\r\n<li><span>Canvas is printed with your photo, laminated, stretched and hand mounted on 1 3/8 inch bars.</span></li>\r\n<li><span>Our beautiful &ldquo;gallery wrap&rdquo; style of canvas requires no frame.</span></li>\r\n</ul>', 1, 3);
INSERT INTO `toon_ez_categories` VALUES(19, 1, 'Rolled Canvas', '19_20x30_Rolled_Cavnas_(90050).jpg', '<p>Rolled Canvas Images are printed to finished size. A<br />3" border is added to the outside of the image to<br />provide extra material for stretching or framing. This<br />border is in addition to the actual image, meaning<br />there is 3" of extra canvas added around the printed<br />product.</p>', 1, 6);
INSERT INTO `toon_ez_categories` VALUES(22, 2, 'Aprons', '22_apronflat_(10181).jpg', '<p>Dad will be grilling out in style.</p>\r\n<ul>\r\n<li><span>Longer &ldquo;chef&rdquo; style apron features your 7.5&rdquo;x9.5&rdquo; image sublimated onto the top front.</span></li>\r\n<li><span>Apron is a white 50/50 cotton/polyester blend and is 12.5" Wide x 33.5" Long.</span></li>\r\n<li><span>A wonderful gift for the barbecuer, gourmet or gourmet wannabe!</span></li>\r\n</ul>', 1, 2);
INSERT INTO `toon_ez_categories` VALUES(23, 2, 'Tote Bag', '23_totebagflat_(10185).jpg', '<p>Personalize this classic tote bag!</p>\r\n<ul>\r\n<li><span>Classic white canvas tote bag with navy bottom and straps.</span></li>\r\n<li><span>Your 5&rdquo;x7&rdquo; image is printed onto the prominent front pocket.</span></li>\r\n<li><span>Tote bag is 14" deep x 19" wide.</span></li>\r\n</ul>', 1, 3);
INSERT INTO `toon_ez_categories` VALUES(21, 3, 'Photo Panels', '21_8x10_Photo_Panel_(10571).jpg', '<p>A gorgeous print sublimated on a "block" that requires no frame.</p>\r\n<ul>\r\n<li><span>Ready to display</span></li>\r\n<li><span>Suitable for any d&eacute;cor. Perfect for homes and offices.</span></li>\r\n<li><span>No glass or laminate that can obscure fine details, mute colors or create annoying reflections.</span></li>\r\n<li><span>Highly resistant to ultraviolet rays, scuffs, scratches, water and fading.</span></li>\r\n</ul>', 1, 9);
INSERT INTO `toon_ez_categories` VALUES(24, 3, 'Coasters', '24_4_Coasters_(10113).jpg', '<p>This set of 4 sleek, custom coasters features your chosen photo.</p>\r\n<ul>\r\n<li><span>Use for hot or cold beverages, with no-slip cork backing.</span></li>\r\n<li><span>These beautiful coasters are approximately 4" x 4".</span></li>\r\n<li><span>A thoughtful and useful gift idea!</span></li>\r\n</ul>', 1, 3);
INSERT INTO `toon_ez_categories` VALUES(27, 3, 'Puzzle with Box', '27_252_Piece_10x14_Puzzle_(10127).jpg', '<p>Create a personalized puzzle with your favorite photo!</p>\r\n<ul>\r\n<li><span>10x14 252 Piece Puzzle comes in pieces in a designer box with your photo on top.</span></li>\r\n<li><span>Great gift for kids, grandparents, bridesmaids, birthdays and anniversaries.</span></li>\r\n</ul>', 1, 5);
INSERT INTO `toon_ez_categories` VALUES(26, 2, 'Jewelry', '26_Necklace_(10600).jpg', '<p>Designer jewelry and accessories personalized with your photo.</p>\r\n<ul>\r\n<li><span>Necklace has a 0.79" x 0.79" charm and 16" long chain</span></li>\r\n<li><span>Charm Bracelets have between 1 and 6 0.79" x 0.79" charms and are 7" long chain with 1" extender</span></li>\r\n<li><span>Keychain has a 1.10" x 1.10" charm</span></li>\r\n<li><span>Ornament is 2.64"x1.93" and includes red ribbon</span></li>\r\n<li><span>Photo charms are 100% waterproof, tarnish-resistance  and will not crack or fade. Material is solid zinc metal plated in  white bronze (lead-free &amp; nickel-free).</span></li>\r\n</ul>\r\n<div class="button"><a><br /></a></div>', 1, 7);
INSERT INTO `toon_ez_categories` VALUES(28, 2, 'Buttons', '28_3_inch_Photo_Button_(85110).jpg', '<p>A fun way to display photos of family, friends, pets&hellip;whatever you can think of!</p>\r\n<ul>\r\n<li><span>3&rdquo; or 4&rdquo; aluminum button with attaching pin on back.</span></li>\r\n<li><span>Your photo is mounted to the button and protected with a clear mylar cover.</span></li>\r\n<li><span>Great party favor.</span></li>\r\n</ul>', 1, 4);
INSERT INTO `toon_ez_categories` VALUES(29, 2, 'Photo Key and Luggage Tag', '29_Luggage__Tag_(10117).png', '<p>Personalized accessory key tag, with your photo.</p>\r\n<ul>\r\n<li><span>Durable fiberglass key tag has rounded edges and an attached metal key ring.</span></li>\r\n<li><span>Key tag is 1.75&rdquo;x2&rdquo;.</span></li>\r\n<li><span>Great team or club gift, or gift for students, parents and grandparents!</span></li>\r\n</ul>\r\n<p>Personalized luggage tag, with your photo.</p>\r\n<ul>\r\n<li><span>Durable fiberglass luggage tag has rounded edges and comes with a flexible rubber strap.</span></li>\r\n<li><span>Luggage tag is approximately 2&rdquo;x3.5&rdquo;.</span></li>\r\n</ul>', 1, 5);
INSERT INTO `toon_ez_categories` VALUES(36, 3, 'Playing Cards', '36_Playingcards_(70002).jpg', '<p>High quality card deck in convenient plastic storage case.</p>\r\n<ul>\r\n<li><span>Back of playing cards are printed with your selected photo.</span></li>\r\n<li><span>Personalize a deck of cards for birthdays, anniversaries, weddings or graduations! </span></li>\r\n</ul>', 1, 4);
INSERT INTO `toon_ez_categories` VALUES(31, 3, 'Keepsake Box', '31_Keepsake.._(10240).png', '<p>In beautiful hardwood with a glossy mahogany finish, this elegant keepsake box is lined in vanilla velvet.</p>\r\n<ul>\r\n<li><span>When you open the box the lid stays up until you close it. </span></li>\r\n<li><span>Box measurements 5.375"x5.375"x2.1875"</span></li>\r\n</ul>', 1, 8);
INSERT INTO `toon_ez_categories` VALUES(32, 3, 'Desktop Organizer', '32_Desk_Organizer_(10241).jpg', '<p>Distinguished hardwood with lacquered mahogany finish makes this desk organizer a classic office accessory.</p>\r\n<ul>\r\n<li><span>Approximately 5" x 5" x 1.75".</span></li>\r\n<li><span>This makes an excellent gift for students and professionals.</span></li>\r\n</ul>', 1, 7);
INSERT INTO `toon_ez_categories` VALUES(37, 3, 'Magnet Packs (Prints)', '37_Magnet_Packs.jpg', '<p>Collectible flexible magnet with your favorite photo.</p>\r\n<ul>\r\n<li><span>Rounded edges and magnetic backing.</span></li>\r\n<li><span>Small magnet is approximately  2&rdquo;x3&rdquo;; large magnet is approximately 3"x5". </span></li>\r\n<li><span>Magnet pack products include envelopes and are  produced on thinner magnet material to ensure reshipment weight meets  postal service requirements.</span></li>\r\n</ul>', 1, 6);
INSERT INTO `toon_ez_categories` VALUES(33, 3, 'Magnets', '33_Gifts_-_Magnets.jpg', '<p>Collectible flexible magnet with your favorite photo.</p>\r\n<ul>\r\n<li><span>Rounded edges and magnetic backing.</span></li>\r\n<li><span>Small magnet is approximately  2&rdquo;x3&rdquo;; large magnet is approximately 3"x5". </span></li>\r\n<li><span>Magnet pack products include envelopes and are  produced on thinner magnet material to ensure reshipment weight meets  postal service requirements.</span></li>\r\n</ul>', 1, 6);
INSERT INTO `toon_ez_categories` VALUES(34, 3, 'Mousepad', '34_mousepad_(10101).jpg', '<p>Personalize a mousepad with your favorite photo!</p>\r\n<ul>\r\n<li><span>Made of high density foam with an easy to clean, stain resistant finish. Approximately 7.75&rdquo;x9&rdquo;. </span></li>\r\n<li><span>Wonderful gift idea for teachers, professionals, students.</span></li>\r\n</ul>', 1, 2);
INSERT INTO `toon_ez_categories` VALUES(35, 4, 'Calendars', '35_18_Month_Calendar_(70305).png', '<p>A custom calendar to enjoy all year long!</p>\r\n<ul>\r\n<li><span>12 or 18 month calendar on 8.5" x 11" quality card stock</span></li>\r\n<li><span>Wire bound spine features your unique images - one per month, plus cover image</span></li>\r\n<li><span>Hole at top of calendar so you can hang in kitchen, office or study</span></li>\r\n</ul>', 1, 0);
INSERT INTO `toon_ez_categories` VALUES(38, 4, 'Greeting Cards (Prints)', '38_Greeting_Cards_(Prints).jpg', '<p>Photo cards are a lightweight alternative to postcards or folded cards.</p>\r\n<ul>\r\n<li><span>Your 4x8 greeting is printed on high quality glossy, matte, or lustre paper.</span></li>\r\n<li><span>Perfect for holiday greetings, save-the-date/engagement/birth announcements and party invitations.</span></li>\r\n</ul>', 1, 3);
INSERT INTO `toon_ez_categories` VALUES(40, 5, 'Photo Books', '40_PhotoBooks.jpg', '<p>A Photo Book to Preserve Memories!</p>\r\n<ul>\r\n<li><span>20 page minimum with the option to add pages to maximum of 60 pages</span></li>\r\n</ul>\r\n<ul>\r\n<li><span>Material: 100# gloss, premium, acid-free archival quality paper</span></li>\r\n<li><span>Sewn binding</span></li>\r\n</ul>', 0, 0);
